	public static void main(String[] args) throws IOException {
		int T=nextInt();
		for(int i=0;i<T;i++){
			int n=nextInt();
			map=new int[20][20];
			ans=0;
			for(int j=0;j<n;j++){
				int x=nextInt();
				int y=nextInt();
				int c=nextInt();
				map[x][y]=c;
			}
//			for(int j[]:map){
