//			}
			for(int j=0;j<20;j++){
				for(int k=0;k<20;k++){
					if(map[j][k]==1){
						dfs(j,k);
					}
				}
			}
			System.out.println(ans);
		}
		
	}
	
	private static void dfs(int x,int y){
		if(x+1>=0&&x+1<20&&y>=0&&y<20&&map[x+1][y]==0){
			map[x+1][y]=5;
			ans++;
		}
		if(x-1>=0&&x-1<20&&y>=0&&y<20&&map[x-1][y]==0){
			map[x-1][y]=5;
			ans++;
		}
		if(x>=0&&x<20&&y-1>=0&&y-1<20&&map[x][y-1]==0){
			map[x][y-1]=5;
			ans++;
		}
		if(x>=0&&x<20&&y+1>=0&&y+1<20&&map[x][y+1]==0){
			map[x][y+1]=5;
			ans++;
		}
	}
private static int nextInt() throws IOException{
		st.nextToken();
		return (int)st.nval;
	}
	private static String next() throws IOException{
		st.nextToken();
		return st.sval;
	}
}

